// Shopping Cart
let cart = JSON.parse(localStorage.getItem('cart')) || [];
let currentFilter = "all";

// DOM Elements
const cartIcon = document.getElementById('cartIcon');
const cartModal = document.getElementById('cartModal');
const closeCart = document.getElementById('closeCart');
const cartItemsContainer = document.getElementById('cartItems');
const emptyCartDiv = document.getElementById('emptyCart');
const cartTotal = document.getElementById('cartTotal');
const cartCount = document.querySelector('.cart-count');
const checkoutBtn = document.getElementById('checkoutBtn');
const beadsContainer = document.getElementById('beadsContainer');
const braceletsContainer = document.getElementById('braceletsContainer');
const menuToggle = document.getElementById('menuToggle');
const closeMenu = document.getElementById('closeMenu');
const mobileMenu = document.getElementById('mobileMenu');
const newsletterEmail = document.getElementById('newsletterEmail');
const subscribeBtn = document.getElementById('subscribeBtn');
const collectionCards = document.querySelectorAll('.collection-card');

// Initialize the page
document.addEventListener('DOMContentLoaded', function() {
    renderProducts();
    updateCartCount();
    
    // Set up event listeners
    setupEventListeners();
    
    // Check if there are items in cart to show total
    if (cart.length > 0) {
        updateCartTotal();
    }
    
    // Initialize smooth scrolling for anchor links
    initSmoothScrolling();
    
    // Set up collection filtering
    setupCollectionFilters();
});

// Set up all event listeners
function setupEventListeners() {
    cartIcon.addEventListener('click', openCartModal);
    closeCart.addEventListener('click', closeCartModal);
    checkoutBtn.addEventListener('click', checkout);
    menuToggle.addEventListener('click', openMobileMenu);
    closeMenu.addEventListener('click', closeMobileMenu);
    subscribeBtn.addEventListener('click', subscribeNewsletter);
    
    // Close cart modal when clicking outside
    cartModal.addEventListener('click', function(e) {
        if (e.target === cartModal) {
            closeCartModal();
        }
    });
    
    // Close mobile menu when clicking on a link
    document.querySelectorAll('.mobile-nav-link').forEach(link => {
        link.addEventListener('click', closeMobileMenu);
    });
}

// Set up collection filter functionality
function setupCollectionFilters() {
    collectionCards.forEach(card => {
        card.addEventListener('click', function() {
            const collectionName = this.querySelector('h3').textContent;
            filterByCollection(collectionName);
            
            // Update active state
            collectionCards.forEach(c => c.classList.remove('active'));
            this.classList.add('active');
            
            // Scroll to products section
            document.querySelector('#beads').scrollIntoView({
                behavior: 'smooth',
                block: 'start'
            });
        });
    });
}

// Filter products by collection
function filterByCollection(collectionName) {
    currentFilter = collectionName.toLowerCase().replace(' ', '-');
    
    // Clear current products
    beadsContainer.innerHTML = '';
    braceletsContainer.innerHTML = '';
    
    // Filter and display beads
    const filteredBeads = products.beads.filter(product => 
        product.collection && product.collection.includes(collectionName)
    );
    
    // If no filtered beads, show all beads
    const beadsToShow = collectionName === "All Collections" || filteredBeads.length === 0 ? 
        products.beads : filteredBeads;
    
    beadsToShow.forEach(product => {
        beadsContainer.appendChild(createProductCard(product, 'Beads'));
    });
    
    // Filter and display bracelets
    const filteredBracelets = products.bracelets.filter(product => 
        product.collection && product.collection.includes(collectionName)
    );
    
    // If no filtered bracelets, show all bracelets
    const braceletsToShow = collectionName === "All Collections" || filteredBracelets.length === 0 ? 
        products.bracelets : filteredBracelets;
    
    braceletsToShow.forEach(product => {
        braceletsContainer.appendChild(createProductCard(product, 'Bracelets'));
    });
    
    // Update section titles
    const beadsTitle = document.querySelector('#beads .section-title');
    const braceletsTitle = document.querySelector('#bracelets .section-title');
    
    if (collectionName !== "All Collections") {
        beadsTitle.textContent = `${collectionName} Beads`;
        braceletsTitle.textContent = `${collectionName} Bracelets`;
        
        // Show count
        const beadsSubtitle = document.querySelector('#beads .section-subtitle');
        const braceletsSubtitle = document.querySelector('#bracelets .section-subtitle');
        
        beadsSubtitle.textContent = `${filteredBeads.length} products in this collection`;
        braceletsSubtitle.textContent = `${filteredBracelets.length} products in this collection`;
    } else {
        beadsTitle.textContent = "Premium Beads";
        braceletsTitle.textContent = "Handcrafted Bracelets";
        
        const beadsSubtitle = document.querySelector('#beads .section-subtitle');
        const braceletsSubtitle = document.querySelector('#bracelets .section-subtitle');
        
        beadsSubtitle.textContent = "Hand-selected beads for your creative projects";
        braceletsSubtitle.textContent = "Ready-to-wear bracelets for every wrist";
    }
    
    // Re-attach event listeners to new product cards
    document.querySelectorAll('.add-to-cart').forEach(button => {
        button.addEventListener('click', function(e) {
            e.stopPropagation();
            const productId = parseInt(this.getAttribute('data-id'));
            addToCart(productId);
        });
    });
}

// Render products on the page
function renderProducts() {
    // Render beads
    products.beads.forEach(product => {
        beadsContainer.appendChild(createProductCard(product, 'Beads'));
    });

    // Render bracelets
    products.bracelets.forEach(product => {
        braceletsContainer.appendChild(createProductCard(product, 'Bracelets'));
    });
    
    // Add event listeners to all "Add to Cart" buttons
    document.querySelectorAll('.add-to-cart').forEach(button => {
        button.addEventListener('click', function(e) {
            e.stopPropagation();
            const productId = parseInt(this.getAttribute('data-id'));
            addToCart(productId);
        });
    });
}

// Create product card HTML
function createProductCard(product, category) {
    const card = document.createElement('div');
    card.className = 'product-card';
    
    const badgeHTML = product.badge ? 
        `<div class="product-badge">${product.badge}</div>` : '';
    
    // Check if product belongs to current filter
    const isInCurrentFilter = currentFilter === "all" || 
        (product.collection && product.collection.some(coll => 
            coll.toLowerCase().replace(' ', '-') === currentFilter));
    
    card.innerHTML = `
        <div class="product-image" style="background-image: url('${product.image}'); background-size: cover; background-position: center;">
            ${badgeHTML}
        </div>
        <div class="product-content">
            <div class="product-category">${category}  ${product.category}</div>
            <h3 class="product-title">${product.name}</h3>
            <p class="product-description">${product.description}</p>
            <div class="product-footer">
                <div class="product-price">$${product.price.toFixed(2)}</div>
                <button class="add-to-cart" data-id="${product.id}">
                    <i class="fas fa-plus"></i> Add to Cart
                </button>
            </div>
            ${product.collection ? 
                `<div class="product-collections">
                    <small>Collections: ${product.collection.join(', ')}</small>
                </div>` : ''
            }
        </div>
    `;
    
    // Add click event to the whole card
    card.addEventListener('click', function(e) {
        if (!e.target.closest('.add-to-cart')) {
            // In a real store, this would navigate to a product detail page
            showProductDetail(product);
        }
    });
    
    return card;
}

