// Add product to cart
function addToCart(productId) {
    // Find product in both categories
    let product = products.beads.find(p => p.id === productId);
    if (!product) {
        product = products.bracelets.find(p => p.id === productId);
    }
    
    if (!product) return;
    
    // Check if product already in cart
    const existingItem = cart.find(item => item.id === productId);
    
    if (existingItem) {
        existingItem.quantity += 1;
    } else {
        cart.push({
            id: product.id,
            name: product.name,
            price: product.price,
            image: product.image,
            quantity: 1
        });
    }
    
    // Save to localStorage
    localStorage.setItem('cart', JSON.stringify(cart));
    
    // Update UI
    updateCartCount();
    
    // Show feedback with a subtle animation
    const addButton = document.querySelector(`.add-to-cart[data-id="${productId}"]`);
    if (addButton) {
        const originalText = addButton.innerHTML;
        addButton.innerHTML = '<i class="fas fa-check"></i> Added!';
        addButton.style.backgroundColor = '#4CAF50';
        
        setTimeout(() => {
            addButton.innerHTML = originalText;
            addButton.style.backgroundColor = '';
        }, 1500);
    }
    
    // Update cart modal if it's open
    if (cartModal.classList.contains('active')) {
        updateCartDisplay();
        updateCartTotal();
    }
}

// Update cart count in header
function updateCartCount() {
    const totalItems = cart.reduce((total, item) => total + item.quantity, 0);
    cartCount.textContent = totalItems;
}

// Open cart modal
function openCartModal() {
    cartModal.classList.add('active');
    updateCartDisplay();
    updateCartTotal();
}

// Close cart modal
function closeCartModal() {
    cartModal.classList.remove('active');
}

// Open mobile menu
function openMobileMenu() {
    mobileMenu.classList.add('active');
}

// Close mobile menu
function closeMobileMenu() {
    mobileMenu.classList.remove('active');
}

// Update cart display in modal
function updateCartDisplay() {
    cartItemsContainer.innerHTML = '';
    
    if (cart.length === 0) {
        emptyCartDiv.style.display = 'flex';
        return;
    }
    
    emptyCartDiv.style.display = 'none';
    
    cart.forEach(item => {
        const cartItem = document.createElement('div');
        cartItem.className = 'cart-item';
        cartItem.setAttribute('data-id', item.id);
        cartItem.innerHTML = `
            <div class="cart-item-image" style="background-image: url('${item.image}'); background-size: cover; background-position: center;"></div>
            <div class="cart-item-details">
                <div class="cart-item-title">${item.name}</div>
                <div class="cart-item-price">$${item.price.toFixed(2)}</div>
            </div>
            <div class="cart-item-controls">
                <button class="quantity-btn decrease">-</button>
                <span class="quantity-value">${item.quantity}</span>
                <button class="quantity-btn increase">+</button>
                <button class="remove-item">
                    <i class="fas fa-trash-alt"></i>
                </button>
            </div>
        `;
        cartItemsContainer.appendChild(cartItem);
        
        // Add event listeners for this cart item
        const decreaseBtn = cartItem.querySelector('.decrease');
        const increaseBtn = cartItem.querySelector('.increase');
        const removeBtn = cartItem.querySelector('.remove-item');
        
        decreaseBtn.addEventListener('click', () => updateQuantity(item.id, false));
        increaseBtn.addEventListener('click', () => updateQuantity(item.id, true));
        removeBtn.addEventListener('click', () => removeFromCart(item.id));
    });
}

// Update cart quantity
function updateQuantity(productId, isIncrease) {
    const item = cart.find(item => item.id === productId);
    
    if (!item) return;
    
    if (isIncrease) {
        item.quantity += 1;
    } else {
        item.quantity -= 1;
        if (item.quantity < 1) {
            removeFromCart(productId);
            return;
        }
    }
    
    // Save to localStorage
    localStorage.setItem('cart', JSON.stringify(cart));
    
    // Update UI
    updateCartDisplay();
    updateCartCount();
    updateCartTotal();
}

// Remove item from cart
function removeFromCart(productId) {
    cart = cart.filter(item => item.id !== productId);
    
    // Save to localStorage
    localStorage.setItem('cart', JSON.stringify(cart));
    
    // Update UI
    updateCartDisplay();
    updateCartCount();
    updateCartTotal();
}

// Update cart total
function updateCartTotal() {
    const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    cartTotal.textContent = total.toFixed(2);
}

// Checkout function
function checkout() {
    if (cart.length === 0) {
        alert('Your shopping bag is empty!');
        return;
    }
    
    // In a real store, this would redirect to a payment processor
    const total = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    const itemCount = cart.reduce((count, item) => count + item.quantity, 0);
    
    const confirmation = confirm(`Proceed to checkout with ${itemCount} item${itemCount !== 1 ? 's' : ''} for a total of $${total.toFixed(2)}?`);
    
    if (confirmation) {
        alert(`Thank you for your order! Your total is $${total.toFixed(2)}.\n\nThis is a demo store. In a real implementation, you would be redirected to a secure checkout page.`);
        
        // Clear cart
        cart = [];
        localStorage.setItem('cart', JSON.stringify(cart));
        
        // Update UI
        updateCartDisplay();
        updateCartCount();
        updateCartTotal();
        
        // Close modal
        closeCartModal();
    }
}

// Newsletter subscription
function subscribeNewsletter() {
    const email = newsletterEmail.value.trim();
    
    if (!email) {
        alert('Please enter your email address.');
        return;
    }
    
    if (!isValidEmail(email)) {
        alert('Please enter a valid email address.');
        return;
    }
    
    // In a real store, this would send the email to a server
    newsletterEmail.value = '';
    alert('Thank you for subscribing to our newsletter! You\'ll receive updates on new collections and exclusive offers.');
}

// Validate email format
function isValidEmail(email) {
    const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return re.test(email);
}

// Initialize smooth scrolling for anchor links
function initSmoothScrolling() {
    document.querySelectorAll('a[href^="#"]').forEach(anchor => {
        anchor.addEventListener('click', function (e) {
            e.preventDefault();
            
            const targetId = this.getAttribute('href');
            if (targetId === '#') return;
            
            const targetElement = document.querySelector(targetId);
            if (targetElement) {
                // Close mobile menu if open
                closeMobileMenu();
                
                // Calculate header height for offset
                const headerHeight = document.querySelector('.header').offsetHeight;
                
                window.scrollTo({
                    top: targetElement.offsetTop - headerHeight - 20,
                    behavior: 'smooth'
                });
                
                // Update active nav link
                updateActiveNavLink(targetId);
            }
        });
    });
}

// Update active navigation link
function updateActiveNavLink(targetId) {
    // Update desktop nav links
    document.querySelectorAll('.nav-link').forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href') === targetId) {
            link.classList.add('active');
        }
    });
    
    // Update mobile nav links
    document.querySelectorAll('.mobile-nav-link').forEach(link => {
        link.classList.remove('active');
        if (link.getAttribute('href') === targetId) {
            link.classList.add('active');
        }
    });
}

// Handle scroll to update active nav link
window.addEventListener('scroll', function() {
    const scrollPosition = window.scrollY + 100;
    const headerHeight = document.querySelector('.header').offsetHeight;
    
    // Get all sections
    const sections = document.querySelectorAll('section[id]');
    
    sections.forEach(section => {
        const sectionTop = section.offsetTop - headerHeight - 50;
        const sectionHeight = section.offsetHeight;
        const sectionId = section.getAttribute('id');
        
        if (scrollPosition >= sectionTop && scrollPosition < sectionTop + sectionHeight) {
            updateActiveNavLink(`#${sectionId}`);
        }
    });
});

